import { createStore } from "vuex";
import posts from '../posts.json';

const store = createStore({
    state () {
        return {
            posts,
            user: {
                username: 'John the Monkey',
                email: 'john.the.monkey@gmail.com',
                password: 'monkey123',
                about: '',
                userPosts: [ posts[0] ]
            }
        }
    },
    mutations: {
    }
});

export default store;
